// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;
pragma experimental ABIEncoderV2;

contract ChatRoom {
  // Room
  struct User {
    bool isExisted;
    string name;
  }

  uint public userCount;
  mapping(address => User) public users;

  modifier inRoom {
    bool hasUser = isInRoom();
    require(hasUser, 'Please join room to send message');
    _;
  }

  function isInRoom() public view returns (bool) {
    return users[msg.sender].isExisted;
  }

  function joinRoom(string memory _name) public {
    userCount++;
    users[msg.sender] = User(true, _name);
  }

  // Message
  struct Message {
    string message;
    address userId;
    uint timestamp;
  }

  uint public messageCount;
  mapping(uint => Message) public messages;

  function sendMessage(string memory _message) public inRoom {
    Message memory newMessage = Message(_message, msg.sender, block.timestamp);
    messages[messageCount++] = newMessage;
  }

  function getMessages() public view returns (Message[] memory) {
    Message[] memory messageList = new Message[](messageCount);
    for (uint i = 0; i < messageCount; i++) {
      Message storage message = messages[i];
      messageList[i] = message;
    }
    return messageList;
  }
}

